import react, {useEffect, Fragment} from "react";
import FullCalendar from "@fullcalendar/react"
import dayGridPlugin from "@fullcalendar/daygrid";
import interationPlugin, {Draggable} from "@fullcalendar/interaction";


const FullCalendarCustom = () => {
    useEffect(()=>{
        let draggableItems = document.getElementById("external-events");
        new Draggable(draggableItems, {
            itemSelector: ".fc-event",
            eventData: function (evenItem){
                let id = eventItem.getAtribute("id");
                let title = eventItem.getAtribute("title");
                let color = eventItem.getAtribute("color");
                return {
                    id: id,
                    title: title,
                    color: color,
                }

            }
        })
    }, []);
    return (
        <Fragment>
            <FullCalendar
                headerToolbar={{
                    left:"prev",
                    center: "title",
                    rigth: "next"
                }}
                height = {650}
                renderDelay = {10}
                eventDurationEditable = {false}
                editable = {true}
                locale = "es"
                dropable = {true}
                plugin = {[dayGridPlugin, interactionPlugin]}
                dragScroll= {false}
                dayMaxEventRows={3}
                >
            </FullCalendar>
        </Fragment>
    )
}
export default FullCalendarCustom
